package bt3;

public class English {
	private String tu;
	private String nghiaTiengViet;
	private String phienAm;
	public English() {
		super();
		// TODO Auto-generated constructor stub
	}
	public English(String tu, String nghiaTiengViet, String phienAm) {
		super();
		this.tu = tu;
		this.nghiaTiengViet = nghiaTiengViet;
		this.phienAm = phienAm;
	}
	public String getTu() {
		return tu;
	}
	public void setTu(String tu) {
		this.tu = tu;
	}
	public String getNghiaTiengViet() {
		return nghiaTiengViet;
	}
	public void setNghiaTiengViet(String nghiaTiengViet) {
		this.nghiaTiengViet = nghiaTiengViet;
	}
	public String getPhienAm() {
		return phienAm;
	}
	public void setPhienAm(String phienAm) {
		this.phienAm = phienAm;
	}
	
}
